import{_ as t,c as e,o as i,a}from"./app.8da8cbf3.js";const u='{"title":"Overview","description":"","frontmatter":{"title":"Overview"},"headers":[{"level":2,"title":"Synopsis","slug":"synopsis"},{"level":2,"title":"Usage","slug":"usage"},{"level":2,"title":"Configuration","slug":"configuration"}],"relativePath":"index.md","lastUpdated":1634217525695}',o={},d=a(`<h1 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-hidden="true">#</a></h1><h2 id="synopsis" tabindex="-1">Synopsis <a class="header-anchor" href="#synopsis" aria-hidden="true">#</a></h2><p><code>dt-cli</code> is a highly customizable dotfile manager.</p><h2 id="usage" tabindex="-1">Usage <a class="header-anchor" href="#usage" aria-hidden="true">#</a></h2><p><code>dt-cli</code> takes a configuration file and issues syncing process defined in the config file. Put your config file at <code>~/.config/dt/cli.toml</code> and run</p><div class="language-shell"><pre><code>$ dt-cli -c ~/.config/dt/cli.toml
</code></pre></div><p>to start syncing. Note the path in this example (<code>~/.config/dt/cli.toml</code>) is also the default path, so the below command (calling <code>dt-cli</code> with no argument) does the same thing as above:</p><div class="language-shell"><pre><code>$ dt-cli
</code></pre></div><p><strong>Other command line options &amp; flags</strong></p><table><thead><tr><th style="text-align:right;">Options/Flags</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:right;"><code>-c|--config-path &lt;path&gt;</code></td><td style="text-align:left;">Specifies path to config file.</td></tr><tr><td style="text-align:right;"><code>-d|--dry-run</code></td><td style="text-align:left;">Shows changes to be made without actually syncing files.</td></tr><tr><td style="text-align:right;"><code>-v|--verbose</code></td><td style="text-align:left;">Increases logging verbosity.</td></tr><tr><td style="text-align:right;"><code>-q|--quiet</code></td><td style="text-align:left;">Decreases logging verbosity.</td></tr><tr><td style="text-align:right;"><code>-h|--help</code></td><td style="text-align:left;">Prints help information.</td></tr><tr><td style="text-align:right;"><code>-V</code></td><td style="text-align:left;">Prints version information.</td></tr></tbody></table><h2 id="configuration" tabindex="-1">Configuration <a class="header-anchor" href="#configuration" aria-hidden="true">#</a></h2><p>See the <a href="/config/guide/">hands-on guide</a> for creating a configuration file.</p>`,12),n=[d];function s(l,r,c,g,h,f){return i(),e("div",null,n)}var y=t(o,[["render",s]]);export{u as __pageData,y as default};
